-- ----------------------------
-- Table structure for introductory
-- ----------------------------
DROP TABLE IF EXISTS `introductory`;
CREATE TABLE `introductory`  (
  `intro_id` int(11) NOT NULL AUTO_INCREMENT,
  `ticket_info` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '门票信息',
  `tips` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '小贴士',
  `favor_policy` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '优惠政策',
  `trans` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '交通信息',
  PRIMARY KEY (`intro_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 16 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

