DROP TABLE IF EXISTS `spot_rank`;
CREATE TABLE `spot_rank`  (
  `rank_id` int(11) NOT NULL,
  `spot_id` int(11) NULL DEFAULT NULL,
  `locale_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `spot_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `spot_img` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `rank` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`rank_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of spot_rank
-- ----------------------------

-- ----------------------------
-- Table structure for spots
-- ----------------------------
DROP TABLE IF EXISTS `spots`;
CREATE TABLE `spots`  (
  `spot_id` int(11) NOT NULL AUTO_INCREMENT,
  `locale_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '地区',
  `spot_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '景点名',
  `avatar` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '景点图',
  `level` char(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '星级',
  `intro` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `ticket_info` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '门票信息',
  `favor_policy` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '优惠政策',
  `open_time` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '开放时间',
  `tips` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '小贴士',
  `trans` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '交通信息',
  `pics` blob NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`spot_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 23 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of spots
-- ----------------------------
INSERT INTO `spots` VALUES (1, '广东广州', '番禺区长隆旅游度假区', '番禺区长隆旅游度假区.png', '5A', '广州市长隆旅游度假区是国家5A级景区。长隆旅游度假区综合了主题公园、文化演艺、科普教育、餐饮酒店、休闲度假等完善的现代服务配套设施，旅游产品结构丰富、主题特色鲜明，加上交通条件便利、游览范围集中、游客接待量大，拉动了周边相关经济发展，取得了较好的社会效益和经济效益，成为广东省旅游文化的名片，品牌效应影响全国、辐射东南亚。长隆旅游度假区包括长隆欢乐世界、长隆水上乐园、长隆国际大马戏、香江野生动物世界、长隆酒店、广州鳄鱼公园、长隆高尔夫练习中心、香江大酒店、香江海鲜酒家等9家子公司，毗邻而建，又各占鳌头。长隆旅游度假区被评为全国旅游先进单位和文化部的文化产业示范基地。\r\n\r\n长隆野生动物世界：以大规模野生动物种群放养和自驾车观赏为特色，拥有包括澳洲树熊、中国大 熊猫等世界各国国宝在内的500余种20000余只珍奇动物。2012年创造性推出动态高仿真实景恐龙园“侏 罗纪森林”及原生态多媒体主题蛇园“金蛇秘境”；2013年再度引领行业潮流，创新推出多媒体互动生 态主题区“丛林发现”和原生态非洲野生特色动物展区“非洲森林”；2013年8月，首只广东熊猫仔在长 隆野生动物世界诞生。长隆水上乐园：连续多年被评为“全球必去水上乐园”。拥有众 多世界金奖水上游乐设施”滑道超级造浪池等。2012年全新开放的“热浪谷”，是亚洲首创的超大户外温水新区。2013年，长隆水上乐，并重磅推出全球首台专门为儿童设计的蛇形亲子滑道。', '普通票:全票250人民币//大学生票215人民币/青少年/学生票200人民币/小童票175人民币/长者票175人民币/优待票175人民币；套票:(7日内畅玩)长隆水上乐园+欢乐世界特惠全票280人民币/(7日内畅玩)长隆水上乐园+欢乐世界大学生票230人民币/(7日内畅玩)长隆水上乐园+欢乐世界青少年/学生票230人民币/(7日内畅玩)长隆水上乐园+欢乐世界特惠小童票205人民币/(7日内畅玩)长隆水上乐园+欢乐世界特惠长者票205人民币/(7日内畅玩)长隆水上乐园+欢乐世界特惠优待票203人民币 (1月1日-12月31日 周一 ~ 周日)\r\n半票:14岁以下且身高1.0米至1.5米儿童。65岁以上老年人凭有效证件\r\n免票:身高1.0米以下的儿童免票，1名成人仅限携带1名免票儿童入园', '青少年（学生票）：适用于身高达到1.5米及以上的未成年人，凭本人学生证件或居民身份证验票入园。大学生票：适用于在校的全日制普通高校的本科生或专科生(含港澳地区大学生)，凭本人有效的学生证验票入园(自考、成人大学、继续教育学院、专业技术学院等非全日制普通高校学生或研究生、博士生不适用此优惠票）', '01月01日-12月31日：全天开放；\r\n补充说明：包含多个子景点，每个景点开放时间不同', '1、园内部分项目不适合高血压、心脏病患者、颈椎背部疾病患者、孕妇、酒醉者游玩；部分项目对参与游客的身高、腰围、体重等设有要求限制，有上述病情或症状者，请自行确认并选择其他适合项目，否则，有可能导致不良后果。\r\n\r\n2、如不慎丢失物品 请直接至游客服务中心询问\r\n\r\n3、为保证安全，根据国家安全法律法规及其他相关规定，园内游乐设备设施将定期进行日检、周检、月检和年度检修，相关检修工作可能会导致部分游乐项目延迟开放或暂停开放，详情请参见乐园公告或园内告示。\r\n\r\n4、在不能满足设备安全运行的恶劣天气，无法抗拒的自然灾害情况下（如雷电、雨雪、冰雹、大雾、暴雨、台风等），园内设备将临时关闭或部分关闭，表演会取消或部分取消。\r\n\r\n5、在园区内不得穿滚轮鞋，不得使用滑板，不得骑自行车等（园区指定表演者在指定场地的演出除外）。\r\n\r\n6、园区内不得带食物', '自驾线路\r\n广州、佛山及粤北地区：各地高速转—广珠西线高速（南屏出口）—南屏检查站—南琴路—横琴大桥—横琴长隆；\r\n\r\n东莞、深圳及粤东地区：各地高速转—京港澳高速—西部沿海高速（湛江方向，月环出口）—广珠西线高速（南屏出口）—南屏检查站—南琴路—横琴大桥—横琴长隆；\r\n\r\n中山、江门及粤西地区：​各地高速转—西部沿海高速（月环出口）—广珠西线高速（南屏出口）—南屏检查站—南琴路—横琴大桥—横琴长隆\r\n\r\n地铁路线\r\n562线（黄埔客运站→长隆欢乐世界总站）\r\n\r\n512线（天河北公交总站→长隆欢乐世界总站）\r\n\r\n530线（沙涌南总站→长隆欢乐世界总站）\r\n\r\n202线（窖口客运总站－长隆欢乐世界总站）\r\n\r\n221线（动物园南门总站←长隆欢乐世界总站）\r\n\r\n247线（动物园南门→长隆欢乐世界总站）\r\n\r\n地铁三号线汉溪长隆站E出口，景区另设免费大巴接驳到长隆旅游景区\r\n\r\n公交路线\r\n可乘坐k10、k11、86、14路到长隆站下车。', NULL, '2021-05-14 21:17:39', NULL);
INSERT INTO `spots` VALUES (2, '广东广州', '白云区白云山景区', '白云区白云山景区.png', '5A', '白云山是南粤名山之一，被称为羊城第一秀、南越第一山。\r\n由30多座山峰组成，登高可俯览全市，遥望珠江，是知名的踏青胜地。\r\n每当雨后天晴或暮春时节，山间白云缭绕，白云山名由此而来。\r\n分为明珠楼、摩星岭、鸣春谷、三台岭、麓湖、飞鹅岭及荷依岭七个游览区。\r\n景区内还能体验滑草、滑道、小型蹦极等娱乐项目\r\n', '白云山:进山票5人民币/汽车进山票15人民币；索道:上山单程票25人民币/下山单程票20人民币；云台花园/鸣春谷:10人民币；碑林/摩星岭/桃花涧:5人民币 (1月1日-12月31日 周一-周日)', '半票:持有效证件的全日制大学本科及以下学生、 6岁至18岁未成年人、60岁至64岁老人\r\n免票:6岁或身高1.2米以下儿童、65岁以上老人、现役军人、军队离退休干部、残疾人凭有效证件', '全天09:00-18:00(索道) (1月1日-12月31日 周一-周日)', '1、注意饮食卫生：提高防护传染病、流行病的意识。注意用餐卫生，不食用不卫生、不合格的食品及饮料，注意环境卫生，尽量少在路边小摊用餐。\r\n\r\n2、做好个人防护：备好相应的服装鞋、帽，做好防晒、防蚊虫等工作。\r\n\r\n3、注意人身安全：请在自己能够控制风险的范围内活动，注意人身安全，旅途中遇紧急情况的，应立即报警并寻求当地警察机关的帮助。\r\n\r\n4、防范水上风险：水上游览或活动时，应加倍注意安全，不可擅自下水或单独前往深水区或危险水域，应听从指挥和合理的劝阻。\r\n\r\n5、保管好贵重物品：贵重物品勿放在交运行李、酒店房间或旅游巴士上，随身携带财物稳妥安置，不要离开自己的视线范围，游览、拍照、散步、购物时，随时注意和检查谨防被盗遗失。\r\n\r\n6、携带旅行票证：个人身份证件、旅行证件、交通票证请随身妥善保管，以避免遗忘、丢失。', '1、在越华路上乘到白云山的专线车；\r\n2、乘11路公交车至沙河濂泉路再步行上山；\r\n3、乘24、257、223、36、285、540路公交车先到云台花园，再乘缆车（全长1672米）上山 。', NULL, '2021-05-14 21:17:51', NULL);
INSERT INTO `spots` VALUES (3, '广东深圳', '南山区华侨城旅游度假区', '南山区华侨城旅游度假区.png', '5A', '深圳华侨城旅游度假区坐落于美丽的南海之滨、深圳湾畔，约六平方公里的土地上，长年繁花似锦、绿树成荫，这里汇聚了中国最为集中的文化主题公园群、文化主题酒店群和文化艺术设施群，这里是中国首批5A级旅游景区、首批全国文明风景旅游区、国家级文化产业示范园区，这里是\"精彩深圳、欢乐之都\"流光溢彩的一张城市名片。以锦绣中华、中国民俗文化村、世界之窗、欢乐谷四大主题公园为核心，深圳华侨城旅游度假区形成了中国最具规模和实力的主题公园群，年均接待游客800万人次。', NULL, NULL, '度假区全天开放，所辖各景点开放时间详见景区公示', '1.游玩过程中，请注意个人安全，保护好儿童及老人，以免发生意外；\r\n\r\n2.做文明乘客，保护景区环境\r\n\r\n3.请遵守景区相关规定。', '地铁：搭乘地铁1号线罗宝线至华侨城站下，D出口出即可。\r\n汽车路线\r\n罗湖火车站有舒适的鹏运观光巴士直达欢乐谷，乘以下线路公交车在世界之窗站下车，步行3分钟即至欢乐谷。\r\n公交路线\r\n大巴：202、126、204、209、210、222、223、101、105、113、301、310、311、320、370\r\n中巴：423、429、432、433、434、439、453、455、456、462、465、502、507、511、541\r\n', NULL, '2021-05-14 21:18:07', NULL);
INSERT INTO `spots` VALUES (4, '广东肇庆', '端州区七星岩', '端州区七星岩.png', '5A', '七星岩位于广东肇庆市区北约2公里处，由五湖、六岗、七岩、八洞组成。景区分为东西南北四个门，东北方还有个仙女湖入口。\r\n\r\n东门离仙女湖比较近，南门比较靠近七星岩牌坊广场，但这两个入口相对而言都离核心景区比较远，所以建议可以从西门进入或者走迎宾大道的北门。\r\n\r\n这就是个公园，比较遗憾的是收费比较贵，你可以在此登高爬山或者亲水游湖。如果你想登高，可以爬上天柱岩或者石室岩，从上而下观望整个湖面。如果你对溶洞感兴趣，可以去石室洞，里面有很多石乳和石柱。除外，洞内也不乏摩崖石刻，600多幅作品，可以看见各种朝代的字体，值得一观。', '七星岩门票成人票-【早订享优惠】入园时间08:00-10:00【【早订享优惠】入园时间08:00-10:00】 ￥65\r\n七星岩门票成人票-【早订享优惠】入园时间10:00-12:00【【早订享优惠】入园时间10:00-12:00】 ￥65\r\n七星岩门票成人票-【早订享优惠】入园时间12:00-14:00【【早订享优惠】入园时间12:00-14:00】 ￥65\r\n七星岩门票成人票-【早订享优惠】入园时间14:00-18:00【【早订享优惠】入园时间14:00-18:00】 ￥65\r\n星湖旅游景区开心100星湖卡 ￥100', '儿童：6周岁（含）以下或身高1.2米（含）以下，免费\r\n老人：年龄60周岁(含)以上凭身份证，半价；60周岁-69周岁老年人凭本人身份证或老年证，优惠票\r\n学生：全日制大学本科及以下学历学生凭本人有效学生证，优惠票\r\n未成年人：6周岁（不含）-18周岁（含）未成年人凭本人身份证，半价\r\n现役军人：凭现役军人证，免费\r\n残疾人：持本人残疾证，免费\r\n新闻记者：凭新闻总署颁发的本人记者证，免费\r\n补充说明：以上优惠半价门票须到景区售票处购买，以上信息仅供参考，具体请以景区当天披露为准。', '全年 08:00-18:00(最晚入园17:30)', '停车场：【七星岩停车场】参考价格：￥10/次；地址：七星岩风景区入口处；库位：200\r\n行李寄存：景区提供行李寄存服务，可前往游客服务中心寄存\r\n园内交通：电瓶车：工作时间07:30-17:30，15/人，运行于整个景区\r\n卫生间：景区内有多个卫生间，西门和北门均标有醒目的指示牌\r\n便利店：景区内设有便利店。', '乘坐8路到肇庆牌坊公园站下', NULL, '2021-05-14 21:18:48', NULL);
INSERT INTO `spots` VALUES (5, '广东肇庆', '鼎湖区鼎湖山', '鼎湖区鼎湖山.png', '5A', '鼎湖山是岭南四大名山之一，于肇庆城区东北18公里。因地球上北回归线穿过的地方大都是沙漠或干草原，所以鼎湖山又被中外学者誉为“北回归线上的绿宝石”。1956年。1979”计划的保护区，建立了“人与生物圈”研究中心，成为国际性的学术交流和研究基地。这里有野生高等植物1856种，其中珍稀濒危的国家重点保护植物23种；以鼎湖山为原生地的植物有30种。鼎湖山多样的生态和丰富的植物为动物提供了充足的食源和良好的栖息环境。因此这里的动物种类和数量也很多，有鸟类178种，兽类38种，其中国家保护动物15种，1998年12月，经中南林学院森林旅游研究中心测定105600个/立方厘米。鼎湖山自唐代以来就是著名的佛教圣地和旅游胜地。公元678年，禅宗六祖惠能的弟子智常禅师在鼎湖山西南之顶老鼎创建白云寺，此后，高僧云集，周围建起三十六招提，前来朝拜、游览的香客、游人越来越多。明崇祯年间，即公元1633年，在和尚在莲花峰建起莲花庵，第二年又迎来高僧栖壑和尚入山奉为主持，重建山门，改莲花庵为庆云寺，到了清代，庆云寺规模越来越大，成为岭南四大名刹之首。鼎湖山因为覆盖着茂密的森林而蕴藏着丰富的泉水，从而造就了千姿百态的流泉飞瀑。幽深的自然景观，东西两溪流形成两大景区：天溪景区、云溪景区和新开发的天湖景区。', '鼎湖山成人票 ￥65\r\n星湖旅游景区开心100星湖卡不限人群 ￥100\r\n肇庆鼎湖山蝴蝶谷游船成人票(不含鼎湖山大门票) ￥29', '鼎湖山景区优待政策：\r\n\r\n1、免票政策：6周岁（含6周岁）以下或身高不足1.2米（含1.2米）的儿童；70周岁以上老人（凭本人身份证或老年证）；现役军人（凭本人现役军人证）；残疾人（凭本人残疾人证）；新闻记者（凭新闻总署颁发的本人记者证）。\r\n\r\n2、优惠政策：半票（39元/人/次）6周岁（不含6周岁）-18周岁（含18周岁）未成年人（凭本人身份证）；60周岁-69周岁老年人（凭本人身份证或老年证）；全日制大学本科及以下学历学生（凭本人有效学生证）。\r\n\r\n蝴蝶谷游船优待政策：\r\n\r\n1、免票政策：身高在1.2米(不含)以下免费；年龄在70岁(含)以上免费；现役军官持军官证免费；残疾人持残疾证免费。\r\n\r\n2、优惠政策：身高在1.2米(含)-1.5米(含)购优惠票；年龄在60岁(含)-69岁(含)购优惠票。\r\n\r\n以上信息仅供参考，具体请以景区当天披露为准。', '01月01日-12月31日：08:00-18:00；\r\n补充说明：观光车：8:00-18:00；\r\n蝴蝶谷游船：9:00-16:30。', '该景区可能有存在高风险的游玩项目，建议您购买相关保险。出行前，建议您仔细阅读旅游安全的相关内容。抵达景区后，也请您不要忽略景区内的游玩提示或安全提示类信息。\r\n\r\n为使您的旅程圆满完成，并普及旅游安全知识及旅游文明公约，特制定《去哪儿旅游告游客注意事项》，出行前，提醒您仔细阅读相关内容。同时，从出行常识、旅游活动和特殊人群三方面为您提供旅游安全指南。', '公交路线\r\n在肇庆市内乘坐3路、15路公共汽车均可到达鼎湖山，在鼎湖山售票处旁有自然保护区入口，须从此入口进入。\r\n\r\n也可以乘坐出租车，车程18公里。肇庆市内有开外鼎湖山的旅游大巴。公交车费用为每人一元。在肇庆市内乘的士，起步价4元，每公里1.6元。市内巴士1.5元/人，到肇庆的鼎湖山门口是4.5元/人。所以上车前要准备好零钞。\r\n\r\n贴士：鼎湖山景区禁止外来车辆进入，但景区附近设有停车场供自驾车人士停车。', NULL, '2021-05-14 21:19:02', NULL);
INSERT INTO `spots` VALUES (6, '北京', '东城区故宫博物院', '东城区故宫博物院.png', '5A', '又名紫禁城，是中国乃至世界上保存最完整，规模最大的木质结构古建筑群，被誉为“世界五大宫之首”。\r\n内廷以乾清宫、交泰殿、坤宁宫后三宫为中心，以及东西两侧的东六宫和西六宫，是封建帝王与后妃居住之所，也就是俗称的“三宫六院”。\r\n故宫内珍藏有大量珍贵文物，据统计有上百万件，占全国文物总数的六分之一。钟表馆每天11点和14点有钟表演示，不可错过。\r\n故宫需要从南到北参观，午门是唯一的入口，出口是东华门和神武门。\r\n又名紫禁城，是中国乃至世界上保存最完整，规模最大的木质结构古建筑群，被誉为“世界五大宫之首”。\r\n内廷以乾清宫、交泰殿、坤宁宫后三宫为中心，以及东西两侧的东六宫和西六宫，是封建帝王与后妃居住之所，也就是俗称的“三宫六院”。\r\n故宫内珍藏有大量珍贵文物，据统计有上百万件，占全国文物总数的六分之一。钟表馆每天11点和14点有钟表演示，不可错过。\r\n故宫需要从南到北参观，午门是唯一的入口，出口是东华门和神武门。', '门票:成人票60人民币/学生票20人民币；内部景点:珍宝馆10人民币/钟表馆10人民币 (4月1日-10月31日 周二-周日)\r\n半票:·60岁以上（含60岁）老年人凭身份证原件，门票半价优惠。\r\n·持有本市社会保障金领取证的人员，门票半价优惠。\r\n免票:·离休干部凭离休证，免票参观。\r\n·6周岁（含6周岁）以下或身高1.2米（含1.2米）以下儿童免票参观。\r\n·残疾人凭残疾人证件，免票参观。\r\n门票:成人票40人民币/学生票20人民币；内部景点:珍宝馆10人民币/钟表馆10人民币 (11月1日-次年3月31日 周二-周日)\r\n半票:60岁及以上老年人、低保户，凭有效证件入园。\r\n免票:离休干部凭离休证、1.2米以下儿童、残疾人、随团导游，凭有效证件入园。', '学生票包含，大中小学生，除去成人教育、研究生。\r\n故宫所有人需凭身份证入园。\r\n故宫门票全部在官网提前十天预售，现场已不设购票窗口，但可微信支付入馆，每天入馆人数与网络售票共享。\r\n“三八”妇女节，女性观众享受门票半价优惠；“六一”儿童节，14周岁以下儿童（含14周岁），免费参观。随同家长一人，享受半价优惠；“八一”建军节，现役军人凭有效证件，免费参观。', '08:30-16:30；停止售票时间:15:30；停止入场时间:15:40 (11月01日-次年03月31日 周二-周日)\r\n08:30-17:00；停止售票时间:16:00；停止入场时间:16:10 (04月01日-10月31日 周二-周日)', '除法定节假日外，全年实行周一全天闭馆；\r\n如果遇到重大活动或特殊情况，开放时间如有改变，建议提前参考官网，安排出行；\r\n遇到法定假期也会延长或缩短开放时间，建议参考官网;', '公交：乘坐1路、2路、52路、59路、82路、99路、120路、126路、观光1线、专2路在“天安门东”站下车然后步行约900米到达午门。\r\n地铁：乘坐地铁1号线在“天安门东”站下车，步行约900米，即可从午门进入故宫。（故宫博物院的南门）\r\n故宫不设停车场，周边的公共停车场也较远，故不建议驾车前来。', NULL, '2021-05-14 21:19:16', NULL);
INSERT INTO `spots` VALUES (7, '北京', '八达岭长城', '八达岭长城.png', '5A', '八达岭长城史称天下九塞之一，是万里长城的精华，在明长城中，独具代表性，八达岭景区以八达岭长城为主，兴建了八达岭饭店、全周影院和由江泽民主席亲笔题名的中国长城博物馆等功能齐全的现代化旅游服务设施。八达岭景区以其宏伟的景观、完善的设施和深厚的文化历史内涵而著称于世，古称“居庸之险不在关而在八达岭”。该段长城地势险峻，居高临下，集巍峨险峻、秀丽苍翠于一体。八达岭是历史上许多重大事件的见证，从八达岭取道大同，再驾返咸阳；肖太后巡幸、元太祖入关、元代皇帝每年两次往返北京和上都之间、明代帝王北伐、李自成攻陷北京、清代天子亲征……八达岭都是必经之地。近代史上，慈禧西逃泪洒八达岭——京张铁路、孙中山先生登临八达岭长城等，都留下了许多历史典故和珍贵的历史回忆。八达岭长城驰名中外，誉满全球。“不到长城非好汉”。迄今。八达岭已接待中外游人一亿三千万，先后有尼克松、里根、撒切尔、戈尔巴乔夫、伊丽莎白、希思等372位外国首脑和众多的世界风云人物，登上八达岭观光游览。', '儿童：身高1.2米(含)以下或6周岁(含)以下凭有效证件，免费；6周岁（不含）~18周岁（含）凭有效证件，半价\r\n全日制大、中、小学生（不含成教）：凭本人有效学生证，半价\r\n老人：60周岁（含）以上凭有效证件，免费\r\n残疾人：凭残疾证（含军残），免费\r\n军人：凭士官证或军官证，免费\r\n离休人员：凭离休证，免费\r\n社会保障金人员：凭社会保障金领取证，免费', '60周岁以上老人与6周岁以下未成年人的门票免费游客，可提前在景区官网进行预约（请勿购票），预约成功后，可凭本人身份证直接检票入园，无需换票。 优惠政策仅针对门票，索道及地面缆车1.2米（不含）以下儿童免费，其他大小同价。 以上信息仅供参考，具体信息请以当天披露为准', '01月01日-12月31日：06:30-16:30', '1. 八达岭长城景区实施全网络实名制预约售票，游客可提前7天起在网上预约购票，预约购票成功后可持身份证直接入园。\r\n\r\n2. 景区关城登城口、南北缆车入口、全周影院售票处、北八楼登城入口可以租借语音导览器。', '公交路线\r\n1.建议根据实时路况，选择京礼或京藏高速自驾出行。\r\n\r\n2.公交 919 路、880 路、877 路八达岭站恢复运行。\r\n\r\n3.市郊铁路 S2 线八达岭站、京张高铁八达岭长城站运行执 行铁路部门相关规定。', NULL, '2021-05-14 21:19:28', NULL);
INSERT INTO `spots` VALUES (8, '北京', '昌平区明十三陵', '昌平区明十三陵.png', '5A', '明朝迁都北京后13位皇帝陵墓的总称，也是全国现存规模最大、帝后陵寝最多皇陵建筑群之一。\r\n景区是个统一的整体，而各陵又自成一体，每座陵墓分别建于一座山前，长陵是明十三陵之首，明成祖朱棣之墓，也是十三陵中最大的皇陵。\r\n长陵博物馆里有各种陪葬出土文物，凤冠、文臣礼帽、男士的玉腰带，发簪，值得一看。\r\n只有长陵，昭陵，定陵，神道四个景区开放，虽然为陵墓，但是目前发掘的只有定陵。\r\n除了历史，去昭陵和神道上走走也是不错的，神道两旁是各种生动的石像，昭陵一带人少安静，适合静静散步。\r\n', '淡季:明定陵40人民币/明长陵30人民币/居庸关长城35人民币/明昭陵/总神道20人民币/银山塔林15人民币；旺季:明长陵45人民币/明定陵60人民币/明昭陵/总神道30人民币/居庸关长城40人民币/银山塔林20人民币；联票:包含定陵、长陵和神路110人民币 (01月01日-12月31日 周一-周日)', '半票:60岁以上老年人凭有效证件\r\n免票:6岁以下或1.2米以下儿童', '08:00-17:30(明长陵/明定陵/总神道),08:00-17:00(居庸关长城(北关入口)),09:00-17:00(居庸关长城(其他入口)),09:00-16:00(银山塔林),08:30-16:00(明昭陵) (4月1日-10月31日 周一-周日)\r\n08:30-17:00(明长陵/明定陵/总神道),08:00-16:30(居庸关长城(北关入口)),09:00-16:30(居庸关长城(其他入口)),09:00-16:00(银山塔林) (11月1日-次年3月31日 周一-周日)', '停车场：【十三陵停车场】参考价格：小车5元/天，大车10元/天；地址：长陵门口的广场；库位：400\r\n卫生间：景区内设有卫生间，分别在大红门、各景点内等附近，并标有醒目的指示牌。\r\n母婴室：明十三陵母婴室设在游客服务中心，可供游客免费使用\r\n餐厅：园内设有餐厅，分别在长陵景区、定陵景区等附近并标有醒目的指示牌\r\n纪念品商店：园内设有纪念品商店，分别在游客服务中心、定陵博物馆大门南侧、神路景区中央、“户曹行署”院内等附近并标有醒目的指示牌。', '北京城区到明十三陵景区路线\r\n1、德胜门乘坐872路公交车直达明十三陵。\r\n2、乘坐地铁昌平线到达终点南邵站，再换乘公交67路到达十三陵。\r\n3、乘坐345支线或886、888路公交车到达昌平东关总站，再换乘314路公交车到达十三陵。\r\n注：明长陵、定陵、神路之间可乘坐公交314路\r\n自驾车：沿京藏高速公路(原八达岭高速公路)到昌平西关环岛后，向北进入京银路，500米后红绿灯向右行驶进入昌赤路，沿途会经过石牌坊、十三陵总神道(简称神路)、七孔桥。到达丁字路口后，直行长陵、左转定陵和昭陵。\r\n北京城区到居庸关长城路线\r\n自驾车：走G6京藏高速公路居庸关(46)出口出即可。\r\n公交线路：\r\n1、德胜门乘坐919、880路公交车直达居庸关长城。\r\n2、在居庸关乘坐879路公交车可到达明十三陵。', NULL, '2021-05-14 21:19:38', NULL);
INSERT INTO `spots` VALUES (9, '天津', '南开区古文化街（津门故里）', '南开区古文化街（津门故里）.png', '5A', '津门故里牌坊位于天津古文化街内，上有彩绘，其彩绘的形式是“烟琢墨石撵玉”式，牌坊心上的文字是“津门故里”（面朝南）和“晴雪”（面朝北）。“津门”是天津的别称，“故里”是老地方的意思，“津门故里”就是天津卫的老地方，有天津卫的发祥地之意。“晴”是天气好的意思，“雪”有“洗”的含义；“晴雪”是刚刚下完雪后的景色，游人如织的街面，比喻人的心情非常纯真，仿佛被洗涤了一样，到这里为天后娘娘进香，为今后的好日子祝福。', '免费', '2020年2月，为致敬奋斗在抗疫一线的医务工作者，自疫情结束恢复营业后至2020年12月31日，对全国医务工作者实行免门票。\r\n全国医务工作者凭本人身份证和有效执业证件（本人医生执业证或本人护士执业证）均可享受这一政策。景区将统一在景区入口处和售票窗口等显著位置标明“对全国医务工作者免门票”的字样；对以团队方式来旅游的医务工作者，可通过提前预约，方便快捷地进入景区', '全年 09:00-17:00', '游览时间：3小时\r\n适合人群：朋友\r\n到达方式：公交\r\n途中景点： 津门故里牌坊> 天后宫 > 民俗文化馆 > 古玩城 > 商业广场 > 官银号 > 沽上艺苑 牌坊 > 玉皇阁 > 通庆里 > 文化小城 > 戏楼', '自驾车:\r\n1、市外（北京市-天津古文化街旅游区）\r\n2、市内\r\n途经：黄河道-南马路-城厢东路-东马路-通北路-张自忠路-景区正门\r\n需时：11分钟\r\n自由行:\r\n1、公交车方式\r\n公交车路线：15路、12路、856路、907路、861路、675路、681路、863路、903路、907路、469路\r\n目的地站点：东马路站\r\n票价：2元/人\r\n班次：10分钟/班\r\n首/末班车时间：06:00-21:00\r\n2、出租车方式\r\n起步价：11元', NULL, '2021-05-14 21:19:48', NULL);
INSERT INTO `spots` VALUES (10, '河北秦皇岛', '山海关区山海关景区', '山海关区山海关景区.png', '5A', '山海关景区位于河北省秦皇岛东北15公里处，是中国华北与东北交通必经的关隘。建于明洪武年间（1381年），是万里长城的最东端，是一座防御体系完整的城关，历史上曾是重要的军事要塞。山海关建城符合“通川之道，要害之处”的古代城市规划原则。该处正处于海陆咽喉要冲，在历史上被称为兵家必争之地。明洪武十四年(1381年)，中山王徐达移关于此，连引长城为城之址，开始修建山海关。山海关城由关城、东罗城、西罗城、南翼城、北翼城、威远城和宁海城七大城堡构成，四周有长4769米、高11.6米、厚10余米的城墙，墙体高大坚实，气势宏伟。在东、西、南、北建有四个城门，城东南隅、东北隅建有角楼，城中间建有雄伟的钟鼓楼。整个卫城建筑规模宏伟，防御工程坚固。山海关是明代创建“卫所兵制”的产物，明代的“屯田制”和改革政策又对山海关的巩固和发展起到了重要的作用。\r\n万里长城是世界奇迹。山海关是长城的起点，而山海关又有\"天下第一关\"之称,是中国名胜古迹荟萃风光椅旋、气候宜人的历史文化古城和旅游避暑胜地。主要景区有长寿山景区、鳄鱼湖、角山长城、老龙头、孟姜女庙、孟姜女苑、悬阳洞、燕塞湖。', '50.00元 联票（含老龙头、天下第一关古城体验游）：120.00元 联票（含孟姜女庙、天下第一关古城体验游）：90.00元 联票（含老龙头、孟姜女庙）：90.00元 联票（含孟姜女庙、孟姜女庙、天下第一关古城体验游）：200.00元', '儿童：6周岁（含6周岁）以下或身高1.2米（含1.2米）以下，免费\r\n老人：70周岁以上老年人，凭有效证件；拥有国务院及部队颁发的离休证的老人；持本市《敬老优待证》的离休干部及地级退休干部、市委任命的正县级退休干部；，免费；60周岁（含60周岁）至69周岁老人凭身份证，优惠\r\n军人：现役军人、残疾军人、军队离休、退休干部及退休士官，凭有效证件，河北省退役军人、烈士遗属、军人遗属凭《河北省退役军人优待证》、《病故军人遗属优待证》，免费\r\n残疾人：凭《中华人民共和国残疾人证》(一级及二级重度肢体 残疾人、盲人、智力残疾人、精神残疾人允许一名陪护人员免费进入)，免费\r\n记者：凭中国新闻出版总署颁发的记者证，免费\r\n导游：持国家级导游证的导游员；，免费\r\n无偿献血者：持《河北省无偿献血荣誉卡》及有效身份证件；，免费\r\n高层次人才：秦皇岛市博士专家联谊会的高层次人才，凭有效证件；持“燕赵英才服务卡”A卡的高层次人才，免费\r\n摄影家协会会员：持本市、区摄影家协会会员证的成员；，免费\r\n消防救援人员：山海关本区消防救援人员持相关证件。，免费\r\n长城使者：对持“长城使者”证书的捐款人及两名家属实行终身免费游览山海关的感恩政策。，免费\r\n未成年人：6周岁（不含6周岁）至18周岁（含18周岁）未成年人凭学生证或身份证，优惠\r\n学生：全日制大学本科及以下学历学生凭学生证，优惠\r\n中国长城学会会员：中国长城学会会员凭会员证，优惠\r\n疫情期间驰援武汉的医务工作者：疫情期间驰援武汉的医务工作者终身免费，凭有效证件及身份证。，免费\r\n补充说明：1、电瓶车优惠票（5元）暂停销售，无优惠政策。 2、符合以上条件的香港、澳门、台湾等入境游青少年凭《港澳居民来往内地通行证》、《台湾居民来往大陆通行证》或学生证等有效证件，享受同等待遇。 3、残疾证分为红皮证和绿皮证。视力残疾为红皮证，其余为绿皮证。证书表面有残疾人标和中华人民共和国残疾人证字样。内容为电脑机打，照片上有钢印，手写已作废。 4、6周岁以下儿童凭户口本或户口本复印件。', '全年 全天开放；古城内部景点8:00-17:00', '停车场：【山海关古城停车场】参考价格：旺季10元/小时，淡季5元/小时；地址：景区附近；库位：750\r\nWIFI：账号：天下第一关；密码：无密码；范围：全景区\r\n行李寄存：山海关游客服务中心提供行李寄存服务，游客可前往咨询。\r\n卫生间：景区内设有卫生间，分别在罗城游客中心、瑞莲阁公园、坡道口、广场等设施附近，并标有醒目的指示牌。【高端服务信息】：山海关包含私人包场套餐、高级导游讲解、高级导游人工讲解', '外部交通：\r\n航空：秦皇岛北戴河国际机场距市区13公里，每周都有到上海、青岛、南京、海口、石家庄、广州的航班。\r\n铁道线路：京哈铁路，秦沈客运专线、津山铁路、沈山铁路。\r\n内部交通：\r\n公路：山海关距海港区仅17公里，主要由京哈高速、102国道、秦山公路、龙海大道连接。\r\n跨区运营的公交线路3条：\r\n25路：山海关火车站~四道桥汽车站（经由龙海大道运行，途经天下第一关、老龙头、乐岛海洋公园）；\r\n33路：山海关汽车站~四道桥汽车站（经由秦山公路运行，途经天下第一关、秦皇岛火车站）；\r\n35路：渤海家园~四道桥汽车站（经由龙海大道运行，途经老龙头、乐岛海洋公园）。\r\n区内运营的公交线路3条：\r\n13路（山海关汽车站~出口加工区）、301路（博兴物流~渤海家园），303路（山海关汽车站~哈电重装）。\r\n另外旅游旺季还会开头旅游专线前往各个景点。\r\n山海关长途汽车站始发直秦皇岛其他县区的长途班车，以及到辽宁省绥中县的省际班车。\r\n', NULL, '2021-05-14 21:20:02', NULL);
INSERT INTO `spots` VALUES (11, '上海', '浦东新区东方明珠广播电视塔', '浦东新区东方明珠广播电视塔.png', '5A', '毗邻黄浦江，与外滩隔江相望，是上海的标志性文化景观之一。\r\n集都市观光、时尚餐饮、购物娱乐、历史陈列、浦江游览、会展演出等多功能于一体，是全国首批5A级旅游景区。\r\n高468米，从上到下有11个球体串联。位于267米球体的旋转餐厅，是360°观赏上海夜景的绝佳去处。\r\n259米处为悬空观光廊。通道采用钢结构，玻璃敷设地面与墙体，可以俯视黄浦江和陆家嘴全貌。', '超值票(259米户外悬空廊+263米举目浦江两岸):成人票199人民币/儿童票99人民币/家庭票(2大1小)497人民币；巅峰票(351米太空舱):成人票220人民币/儿童票110人民币；门票(超值票)+餐饮(旋转餐厅):成人票368人民币/儿童票184人民币 (1月1日-12月31日 周一-周日)', '半票:1m-1.4m儿童，看上海单层票: 现役军人、离退休干部、军残证、烈属证(凭有效证件)\r\n免票:1m以下儿童', '09:00-21:00；停止入场时间:21:00 (01月01日-09月30日,10月09日-12月31日 周一-周日)\r\n08:00-21:30；停止入场时间:21:30 (10月01日-10月08日 周一-周日)', '行李寄存：景区4.2米检票口。\r\n景区讲解：景区提供景区讲解服务，费用：350/人3人起。\r\n卫生间：景区内设有多个卫生间，在景区内均标有醒目的指示牌。\r\n餐厅：东方明珠内设有多个餐厅，分别在不同观光层并标有醒目的指示牌。\r\n纪念品商店：东方明珠内设有多个纪念品商店，在0米大厅附近并标有醒目的指示牌。', '公共交通\r\n1.地铁2号线陆家嘴站可到达 。\r\n2.公交81、82、85、583、774、789、791、795、796、798、870、871、983、993、971、985、993、蔡陆专线、陆家嘴金融城1路、 陆家嘴旅游环线、观光隧道、轮渡等可到达。', NULL, '2021-05-14 21:20:12', NULL);
INSERT INTO `spots` VALUES (12, '上海', '浦东新区上海野生动物园', '浦东新区上海野生动物园.png', '5A', '上海野生动物园是首批国家5A级旅游景区，是野生动物们的温馨家园，是大都市里的天然氧吧。这里居住着来自国内外的珍稀野生动物200余种，上万余头（只），它们在此快乐生活、繁衍生息。园区分为步行区、车入区及“水域探秘”三大部分。您可以在步行区邂逅大熊猫、非洲象、亚洲象、长颈鹿、火烈鸟、袋鼠、环尾狐猴、企鹅等众多动物，也可以参与诸多富有特色的动物科普讲堂，让您更加了解和喜爱这些新结识的动物朋友。车入区为动物散放养展示形式，给动物们更多的自由空间，也让游客沉浸在大自然中，观赏动物们觅食、玩耍的自然行为，其乐融融。“水域探秘”是360度乘船无遮挡游览方式，在水上看动物，近距离观赏各种珍稀动物和大批候鸟，开启“慢生活”给您带来全新的观赏体验。为更好地宣传野生动物保护理念，丰富动物科普知识，让更多人爱上动物，上海野生动物园推出了“动物科普讲解”项目。在主要的动物展区，每天以固定时间由动物保育员给游客讲述动物科普知识以及他们和动物之间发生的有趣故事。', '成人票130元（官网订票120元）；儿童票65元；学生票65元，全日制本科生需携带学生证及副卡。', '特殊人群： A.免费政策：儿童身高1.3米以下或6周岁以下免票；现役军人免票，离休干部免票。\r\nB.优惠政策：65周岁以上老人凭有效证件购买老人票65元/人。\r\n60~64周岁老年人凭有效证件购买老人票117元/人', '3月1日-6月30日 09:00-17:00；7月1日-8月31日 09:00-20:00；9月1日-11月30日 09:00-17:00；12月1日-次年2月28日 09:00-16:30', '春秋两季是野生动物园最佳旅游时节，建议上午早点入园，因为动物大多上午会比较活跃，而且通常下午的游客会比上午多。\r\n进入车入区（放养区）需坐园区的游览车，间隔20分钟发一趟车，若人满也会提前开车，游览需半小时。游览车分为普通车和投喂车（可在车中向动物投食），前者已含在门票中，后者需另收费（约每成人35元，不含饲料）。\r\n可以喂食的小动物区域每个园区一般需另行收费（约每人10-20元）。\r\n4. 6D电影时长约10分钟，需另行购票，影院有时会进行维护暂停开放，具体以影院当天公告为准。', '公交车\r\n上海张南专线公交车路线、上海南新专线公交车路线、上海惠南6路公交车路线 [4] \r\n专线\r\n南新：上海火车站南广场→南汇大学城 野生动物园站下车\r\n张南：张江地铁站→南汇大学城 野生动物园站下车\r\n地铁\r\n上海轨道交通16号线（野生动物园站下车）\r\n外部交通\r\n飞机、火车、汽车、自驾。\r\n自驾车\r\n（1）市区--南浦大桥--龙阳路--沪南公路--南六公路\r\n（2）市区--南浦大桥--龙阳路--罗山路--外环线（S20公路，浦东国际机场方向）--迎宾大道（S1公路）--南六公路\r\n（3）市区--南北高架--卢浦大桥--济阳路--外环线（S20公路，浦东国际机场方向）--沪芦高速公路（S2公路）--沪南公路--南六公路', NULL, '2021-05-14 21:20:26', NULL);
INSERT INTO `spots` VALUES (13, '上海', '浦东新区上海科技馆', '浦东新区上海科技馆.png', '5A', '上海科技馆位于浦东世纪大道，是上海较大的科普教育殿堂。在这里可通过各种现代化设备、丰富的展示互动方式，全方位了解自然科学，更有声像效果震撼的科学影城。如果带着孩子来上海，这里一定是寓教于乐的必游之地。\r\n\r\n上海科技馆展品丰富，这里有数百件精美的野生动物标本与地质标本、有各种功能迥异的机器人表演、各种电子信息设备与航天设备模型，以及多种科技发明的展示。而参与性强更是青少年游客爱来的原因，游客可通过互动设备来认识数理化等学科的多种规律和原理、可亲手参与一些简单的设计和制造活动、可通过声光电的多种场景模拟看到地震和火山喷发等自然奇观、还可参与各种运动模拟来了解人体与健康的奥秘等等。\r\n\r\n科技馆还拥有IMAX立体巨幕、球幕、四维和太空数字四大高科技特种影院组成的科学影城，家长可以带上小朋友们亲身体验极具动感和真实效果的科幻场景，感受高科技的魅力。科学影城放映场次可参考官网。', '儿童： 身高1.3米（含）以下儿童（需有成年人陪护，一名成人限带2名儿童），免费\r\n老人：70周岁以上老年人凭老年证或身份证，免费；年龄：60周岁（含）~69周岁（含）（凭本人有效证件购票入场），优惠票\r\n其他：70周岁及以上老人、离休干部、现役军人、国家综合性消防救援人员、残疾人、烈士家属凭相关有效证件入场，免费\r\n学生：1.3米以上儿童、6周岁以上至18周岁未成年人，全日制大学本科及以下学历学生(凭本人学生证购票入场)，优惠票', '新增免票人群：因公牺牲军人遗属、病故军人遗属；在职、退休、残疾消防救援人员（含政府专职消防员）和消防救援院校学员，须持有效证件检票核验入场，免费\r\n新增优惠人群：现役军人家属（含现役军人的配偶、未成年子女、不能独立生活的成年子女、父母(扶养人)）给予享受门票优惠，须携带相关身份证明检票核验入场，优惠票\r\n补充说明：上海科技馆馆内的球幕电影、立体电影：无免票及优惠人群，婴幼儿谢绝观看，儿童老人与成人同价。', '全年 周二至周日 09:00-17:00(最晚入园16:00) 周一 不开放；中国法定节假日 09:00-17:00；周一闭馆（国家法定节假日正常开放）', '1. 科技馆2号门的游客服务中心可以租赁中英文语音导览器，每台租金20元、押金200元。\r\n2. 上海科技馆很大、体验项目很多，尤其节假日时很多项目都需排队等候，因此建议来沪游客尽量避开“十一”、“五一”等重要节假日。\r\n3. 地下一层、地上一层和二层都有游客中心，有免费提供轮椅车、婴儿车、饮用水、行李寄存等服务。', '地铁二号线：上海科技馆站\r\n地铁四号线：“世纪大道站”转地铁二号线\r\n公交线路：东周线，隧道三线、申崇二线、申崇四线，公交640、794、815、984、638、983、184、975路', NULL, '2021-05-14 21:20:45', NULL);
INSERT INTO `spots` VALUES (14, '江苏常州', '新北区中华恐龙园', '新北区中华恐龙园.png', '5A', '中华恐龙园，创立于2000年，是环球恐龙城的核心景点，是一座集科普、游乐、演艺、住宿、购物于一体的一站式旅游度假区，有“东方侏罗纪”之称。\r\n美国咨询机构艾奕康（AECOM）联合主题娱乐协会（TEA）发布《2018全球主题公园和博物馆报告》 [1]  。报告显示，常州中华恐龙园以27.9%的增幅，成为亚太地区前20名主题公园中游客数量增速最快的企业；长隆欢乐世界和珠海长隆海洋王国则以11.9%和10.6%的增速位列第二、第三名。上海迪士尼乐园和香港迪士尼乐园位列亚太地区主题公园第四、第六名。', '儿童：身高1.2米（不含）以下，免费；1.2米（含）-1.5米（不含），优惠票\r\n老人：70周岁（含）及以上老人凭有效身份证件，免费；60周岁（含）-69周岁（含）凭有效证件，优惠票\r\n学生：仅限于全国普通高等院校（港澳台除外）的全日制本科、专科在校生（不含硕士、博士、继续教育学院学生、自学考试教育学生、夜校大学生、函授教育生、网络教育生、各种在职教育及其他成人进修课程学生），学生票', '残疾人：凭有效身份证件每日每证限换白天门票一次），免费\r\n军人：现役军人、残疾军人凭有效身份证件每日每证限换白天门票一次，免费\r\n记者：凭国家新闻总署、国家广电总局颁发、经核实有效的证件，免费\r\n补充说明：以上信息仅供参考，具体信息请以景区当天披露为准。', '全年 09:00-17:00', '1. 有时在重大节假日期间园内会有花车巡游，非常热闹，建议“五一”、“十一”之前可关注景区官网动态。\r\n\r\n2. 节假日期间及暑期游客很多，尤其小长假的时候各个项目排队可能要很久，建议避开人群出游，或先去玩人少的项目，另需注意避暑防晒。\r\n\r\n3. 恐龙园内的汉堡屋、快餐厅、冷饮站、小吃店很多，每个游玩区域都有，价格可以接受，所以餐饮不用愁；其中“侏罗纪餐厅”、“玛丽的餐厅”等有供应中式套餐。景区内还有多个纪念品商店，里面的恐龙公仔比较受欢迎，还有恐龙杯子、水壶、相框等物件。\r\n\r\n4. 在恐龙园疯玩一天后，还可以去旁边的恐龙谷温泉度假村泡个汤、住一晚。', '公交线路\r\n到达恐龙园的公交线路有4条：302路、50路、53路、99路\r\n常州站（南广场）：\r\n出站后穿过地下通道在“常州客运中心（东）”公交站乘坐Y2路至“龙沧路河海路”站，下车后向北走约100米到达中华恐龙园。\r\n常州站（北广场）、常州客运中心：\r\n出站后在马路对面“常州客运中心（东）”公交站乘坐Y2路至“龙沧路河海路”站，下车后向北走约100米到达中华恐龙园。\r\n常州北站（高铁站）：\r\n在常州北站公交枢纽乘坐B10至“龙锦路天目山路（水木年华）”转乘50路到达中华恐龙园。\r\n常州机场：\r\n32路公交车到“锦绣东苑”站转302路到达中华恐龙园。 [6] \r\n自驾游\r\nG42沪蓉高速：\r\n路线一：\r\n第15号出口（常州江阴出口）下——沿黄河路往东行驶——至龙业路向南行驶——至河海东路向东行驶到达中华恐龙园\r\n路线二：\r\n第15号出口（常州江阴出口）下——沿通江路往南行驶-——至河海路往东行驶到达中华恐龙园\r\n2、沿江高速：\r\n路线一：\r\n第12号出口（沪宁高速出口）——转G42沪蓉高速至第15号出口（常州江阴出口）下——沿通江路往南行驶-——至河海路往东行驶到达中华恐龙园\r\n路线二：\r\n第12号出口（沪宁高速出口）——转G42沪蓉高速至第15号出口（常州江阴出口）下——沿黄河路往东行驶——至龙业路向南行驶——至河海东路向东行驶到达中华恐龙园\r\n', NULL, '2021-05-14 21:20:56', NULL);
INSERT INTO `spots` VALUES (15, '江苏苏州', '苏州拙政园', '苏州拙政园.png', '5A', '拙政园是苏州有名的园林，全园以水为中心，萦绕错落有致的假山及精致的庭院建筑，花木并茂。这一江南园林典范与北京颐和园、承德避暑山庄、苏州留园并称为“中国四大名园”，并被列入世界文化遗产名录。\r\n\r\n整个园林的设计十分精巧，游览起来可以说是“一步一景”，处处体现着江南水乡的韵味。园中小径曲折，从一重重门廊、镂空图案的石墙，到每个亭子、每扇窗户都不雷同，而且与树木花草搭配得恰到好处，构成一幅幅如画般的风景，你可以花上很多时间来摄影。', '全价票:旺季80人民币/淡季70人民币；半价票:旺季40人民币/淡季35人民币 (1月1日-12月31日 周一-周日)\r\n半票:·6周岁（不含6周岁）-18周岁（含18周岁）未成年人、全日制大学本科及以下学历在校学生凭居民身份证或学生证等有效证件实行半价优惠\r\n·60周岁（含60周岁）-70周岁（不含70周岁）老人，凭有效居民身份证或《老年人优待证》实行半价优惠', '免票:1.中华人民共和国现役军人凭军人证等有效证件、军队离退休干部凭离退休干部证免费入园。\r\n2.残疾人员凭《残疾证》免费入园，重度残疾人员需要陪护的，可由一名陪护人员免费陪同入园。\r\n3.70周岁（含70周岁）以上老人，凭有效居民身份证或《高龄证》免费入园。\r\n4.身高1.4米（含1.4米）以下儿童，免费入园。\r\n5. 6周岁（含6周岁）以下儿童，凭有效证件，免费入园。\r\n6. 本市、省内60周岁（含）-70周岁（不含）的老年人凭《老年人优待证》或身份证等有效证件，游览风景名胜区（虎丘、上方山森林公园、上方山森林动物世界、天平山），怡园，免费入园', '07:30-17:30 (03月01日-11月15日 周一-周日)\r\n07:30-17:00 (11月16日-次年02月29日 周一-周日)', '特色活动\r\n杜鹃花节：每年春季，拙政园都要隆重举办一年一度的“杜鹃花节”。“杜鹃花节”既是拙政园的一项传统特色项目，也是被苏州市政府列入“苏州旅游节”的重点项目之一。杜鹃花展期间，将有数百种名贵杜鹃花展出。届时，拙政园内是莺歌燕舞，万紫千红，满目妖娆。不仅让游客在浓浓春意中领略游园的意趣，也让游客在山花烂漫中感受到拙政园这座城市山林的诗情画意。 [29] \r\n荷花节：每年夏季，拙政园都将举办苏州园林的传统特色项目“荷花节”。“荷花节”依托于拙政园宽阔的荷塘，以数百个品种的缸荷、碗莲及多种水生植物造景，渲染荷花香远益清的感官效果，营造夏日江南水乡的清凉意境。此时的拙政园内，到处是‘青荷盖绿水，芙蓉披红鲜’的景象。在河堤绿荫下，在习习凉风中，拙政园成为游客赏荷的最佳去处，在拙政园观赏荷花展已成为苏州市民喜闻乐见的活动，成为了一种“新民俗”。 [30] \r\n反季菊花展：每年秋季，拙政园都将以菊花为主题举办反季菊花展。菊花展中，如彩球，似花灯，像礼花，团团簇簇、千姿百态的菊花，展示着超凡脱俗，孤标亮节，高雅傲霜的自然特质。游客在以疏朗自然的风格著称的拙政园中赏菊，别有一番“采菊东篱下，悠悠见南山”的闲情逸趣。', '汽车北站至拙政园：\r\n方案一：汽车北站出站口乘坐529路到达苏州博物馆（拙政园、狮子林），向东步行约500米到拙政园。\r\n方案二：汽车北站出站口乘坐55路到达苏州博物馆（拙政园、狮子林），向东步行约500米到拙政园。\r\n方案三：汽车北站出站口乘坐游5路到达苏州博物馆（拙政园、狮子林），向东步行约500米到拙政园。\r\n汽车南站至拙政园：\r\n方案一：汽车南站出站口乘坐529路到达苏州博物馆（拙政园、狮子林），向东步行约500米到拙政园。\r\n方案二：汽车南站出站口乘坐811路到达苏州博物馆（拙政园、狮子林），向东步行约500米到拙政园。\r\n方案三：汽车南站向西步行700米到达南环新村站，乘坐55路到达苏州博物馆（拙政园、狮子林），向东步行约500米到拙政园。', NULL, '2021-05-14 21:21:11', NULL);
INSERT INTO `spots` VALUES (16, '四川阿坝藏族羌族自治州', '九寨沟县九寨沟景区', '九寨沟县九寨沟景区.png', '5A', '九寨沟：世界自然遗产、国家重点风景名胜区、国家AAAAA级旅游景区、国家级自然保护区、国家地质公园、世界生物圈保护区网络，是中国第一个以保护自然风景为主要目的的自然保护区。\r\n九寨沟位于四川省阿坝藏族羌族自治州九寨沟县境内。地处青藏高原、川西高原、山地向四川盆地过渡地带，南距成都市300多公里，是一条纵深50余千米的山沟谷地，总面积64297公顷，森林覆盖率超过80%。因沟内有树正寨、荷叶寨、则查洼寨等九个藏族村寨坐落在这片高山湖泊群中而得名。\r\n九寨沟国家级自然保护区主要保护对象是以大熊猫、金丝猴等珍稀动物及其自然生态环境。有74种国家保护珍稀植物，有18种国家保护动物，还有丰富的古生物化石、古冰川地貌。', '旺季4-11月：190元淡季11-3月：80元', '军人：现役军人、伤残军人，抗美援朝军人，凭颁发的本人有效证件，门票免费\r\n残疾人：凭残疾证，门票免费\r\n离休干部：凭本人离休干部相关证件，门票免费\r\n宗教人士：持省外省级中教团体统一印发的皈依证等有效证件的同一宗教信教群众，门票优惠', '4月1日-11月15日 07:30-17:00(最晚入园12:00)；11月16日-次年3月31日 08:30-17:00；旺季：4月1日-11月15日；淡季：11月16日-次年3月31日', '1. 景区属于高原气候，早晚温差很大，无论何时都要记得携带保暖衣物。这里阳光紫外线很强，需注意防晒。\r\n2. 景区海拔较高，部分游客可能会发生高原反应。景区内有多处吸氧点，一般的高反症状吸氧后即可恢复。吸氧免费，但吸氧工具需要购买。', '①【成都-九寨沟】\r\n客运：1.成都新南门车站每天6:30、7:00、7:30、8:00、8:10有班车发往九寨沟景区，车费145元/人，乘车时间10小时左右，电话028-85433609\r\n2.成都茶店子汽车站每天8:40前有3个班次直达九寨沟景区，车程10小时左右，票价120元/人(车况较好的票价为170元/人)\r\n包车：从成都包车到九寨沟价格在1200-1500元之间。\r\n自驾：九寨沟的重要陆路交通是九环线，全长约920公里。\r\n1.九环西线由成都出发，经都江堰、汶川、茂县、松潘至九寨沟，全程约440公里。\r\n2.九环东线由成都出发，经绵阳、江油、平武、九寨沟县城至九寨沟，全程约480公里。\r\n②【九黄机场-九寨沟】\r\n九黄机场位于阿坝州松潘县川主寺镇，距离九寨沟沟口还有约88公里，九黄机场问询电话：0837-7243770。\r\n客运：1.5小时车程，票价45元/人，不过班次较少。\r\n包车：路程88公里左右，车费200元左右。如果想先到黄龙游玩再到九寨沟，全程包车价500元左右。\r\n③【黄龙景区-九寨沟】\r\n客运：黄龙景区每天7:00和7:30有车发往九寨沟景区的班车，车费45元/人，车程约1小时', NULL, '2021-05-14 21:21:22', NULL);
INSERT INTO `spots` VALUES (21, '四川成都市都江堰市', '都江堰旅游景区', '都江堰旅游景区.png', '5A', '两千多年前由秦国蜀郡太守李冰修建的都江堰水利工程，是年代较久、以无坝引水为特征的水利工程，也是目前中国保存较完整的古代水利工程，工程位于都江堰市城西的岷江干流上。\r\n\r\n工程由鱼嘴分水堤、飞沙堰溢洪道、宝瓶口引水口三大主体工程和百丈堤、人字堤等附属工程构成，把汹涌的岷江分隔成外江和内江，外江排洪，内江引水灌溉，使川西平原成为“水旱从人”的“天府之国”。两千多年来，一直发挥着防洪灌溉作用。都江堰水利工程周边景色秀丽，文物古迹众多，主要景点有伏龙观、二王庙、李冰石刻像、安澜索桥、玉垒山公园等。\r\n\r\n1982年，都江堰作为四川青城山-都江堰风景名胜区的重要组成部分，被列入第一批国家级风景名胜区名单。2000年联合国世界遗产委员会第24届大会上，根据联合国《保护世界文化和自然遗产公约》第一条第二款有关文化遗产定义的规定，由于都江堰水利工程历史悠久、规模宏大、布局合理、运行科学，且与环境和谐结合，在历史和科学方面具有突出的普遍价值，因此都江堰被确定为世界文化遗产。', '80元 /人', '儿童：6周岁（含）以下或身高1.3米以下（含）凭有效证件或实际身高，免费\r\n老人：60周岁（含）-64周岁（含）老年人在非国家法定节假日凭有效身份证，免费；65周岁（含）以上的老年人凭有效身份证，免费\r\n残疾人：持由中国残疾人联合会制发的本人残疾证（盲人和重度肢体、智力、精神残疾人限随行1名陪护人员免票），免费\r\n上海市民：上海市民凭身份证或居民户口簿等有效证件（在都江堰离堆大门售票窗口和青城山前山景区售票窗办理），优惠\r\n军人：现役军人，军队离休、退休干部和退休士官凭有效证件，免费\r\n记者：记者凭新闻总署年审通过的记者证，免费\r\n导游及相关从业人员：导游、旅行社经理持本人全国导游证或旅行社经理资格证配合运行计划表，免费\r\n全国公安民警、公安烈士遗属、因公牺牲人民警察父母（抚养人）配偶及子女：公安民警持本人人民警察证及身份证；公安烈士遗属持县级人民政府民政部门发放的《中华人民共和国烈士证明书》及身份证；因公牺牲人民警察遗属需提供由所在单位的县级以上政法机关发放的《中华人民共和国人民警察因公牺牲证明书》及身份证，免费\r\n未成年人：6周岁（不含）至18周岁（不含），半价\r\n学生：在读全日制大学本科以下（含本科）学历学生凭学生证（不含成人教育），半价\r\n持青城前山、都江堰景区游览年票：本人相貌与游览年票照片系必须相同，并且年票在有效期内，免费\r\n补充说明：60周岁（含）-65周岁（不含）的老年人在国家法定节假日（元旦1天、春节3天、清明1天、端午1天、五一1天、中秋1天、国庆3天）持本人身份证享门票半价优惠。 以上信息仅供参考，具体以都江堰景区当天披露为准。', '\r\n3月2日-11月30日 08:00-18:00(最晚入园17:30)；12月1日-次年3月1日 08:00-17:30(最晚入园17:00)；网络票取票时间：08:30-17:00', '停车场：【都江堰离堆公园P1停车场】参考价格：10元/次；地址：四川成都市都江堰离堆公园；库位：300\r\n园内交通：观光电瓶车：碑亭，工作时间8:30-17:00，票价往返15元，单程10元，服务范围为碑亭-鱼嘴，儿童需成人陪同。\r\n卫生间：景区内设有多个卫生间，游客中心及主干道上标有醒目的指示牌。\r\n纪念品商店：都江堰景区内设有多个纪念品商店，具体位置可在景区官网上查看。\r\n便利店：都江堰景区内设有多处便利店，方便游客购物。', '乘坐都江堰4路到离堆公园(都江堰景区)站下', NULL, '2021-05-14 21:21:41', NULL);
INSERT INTO `spots` VALUES (22, '西藏拉萨市', '拉萨市城关区布达拉宫景区', '拉萨市城关区布达拉宫景区.png', '5A', '布达拉宫作为西藏的象征，是所有朝圣者心中的圣地！“布达拉”是梵语，又译作“普陀”，原指观音菩萨之居所。\r\n来这里，不仅可以观赏宫内收藏的大量历史文物，欣赏藏民族精彩的建筑艺术，更可以在那种圣洁的氛围中进行一次灵魂的洗礼。\r\n布达拉宫由来：\r\n据史书记载，公元七世纪三十年代，吐蕃第三十三代赞普松赞干布迁都拉萨，始建布达拉宫。此后，重大的宗教、政治仪式均在此举行，成为西藏政教合一的统治中心。\r\n白宫和红宫：\r\n布达拉宫具有鲜明的藏式风格，气势雄伟，是西藏地区现存完整的宫堡式建筑群，被誉为世界十大土建筑之一。其主体建筑分布宫白宫和红宫两部分。白宫因外墙为白色而得名，是喇嘛们生活起居和政治活动的地方。由于这里终日阳光普照，故称东、西日光殿。红宫位于布达拉宫的顶部中央位置，外墙为红色，由众多灵塔和各个佛殿组成。', '旺季门票:200人民币 (5月1日-10月31日 周一-周日)\r\n淡季门票:100人民币 (11月1日-次年4月30日 周一-周日)', '儿童：国内身高1.2米（不含）以下的儿童，免费\r\n军人：现役军人，半价\r\n老人：60周岁及以上，半价\r\n残疾人：，半价\r\n补充说明：持优惠证件游客，必须在参观当日带上您的优惠证件及身份证，验证后导游现退，否则，一律按网购失信处理，所购门票作废，也无法再次购买参观门票。 西藏自治区布达拉宫管理处对《布达拉宫优惠票适用对象须知》享有解释权。', '全年 09:00-16:00(最晚入园15:00)；15:00停止入场。旺季闭园时间会依据当天客流量大小适当延长，具体时间以景区官方为准。淡季闭园时间会依据当天客流情况适当提前，具体时间以景区官方为准。入园时间以导游通知为准，晚于约定的时间将无法入园参观', '1. 布达拉宫旺季（5月1日至10月31日）实行门票预订制，需要在此期间参观的游客（网上购票和预定除外）需要现场领取预约券。预约时限仅为提前一日预定次日门票，预约时间为每日09:00，预约券领完为止，免费领取。预约地点为布达拉宫管理处唯一门票预约窗口预约。有效凭证为身份证、现役军人证或护照。提前预约的游客，由于个人原因，当天不能参观或不能按时参观的，需及时前往布达拉宫预约窗口申请延期或取消预约。\r\n2. 通过布达拉宫官网购票也很方便，无需提前换票，但需凭本人身份证通过各闸机口。\r\n3. 布达拉宫参观需要遵循所有藏传佛教的禁忌：进大殿后不能戴帽和墨镜，尤其不能穿裙子，穿裙子的游客会被要求换上裤装方可入内。大殿内不能拍照，也不能用脚踩踏门槛。\r\n4. 进布达拉宫会接受严格的安检，管制刀具、易燃易爆物品、液体饮料等都不能带入，大的包也有可能被要求打开检查。. 进入布达拉宫前先解决好方便问题，因为从进门开始一直到售票处德央夏广场右侧才有厕所，之间约30-40分钟的参观路程一路都没有厕所。\r\n5. 布达拉宫正门进入后仅是安检，要到真正的售票处还需要徒步很多台阶，体力差的人可能要走半小时。所以要预留出时间，不要错过购票凭证标记的购票参观时间。\r\n6.布达拉宫的夜景灯在夜里会熄灯，22:30关闭灯源，想要拍夜景的建议在这之前赶到。\r\n\r\n', '航空\r\n西藏辟有国际及省际航线。与成都、北京、上海、重庆、西安、西宁、广州、昌都、昆明以及尼泊尔加德满都之间有航线，游客进出西藏非常便捷。\r\n公路\r\n拉萨的国营长途汽车站位于民族路和金珠中路交界。这里的长途汽车线四通八达，东至八一、昌都、成都，西至日喀则、江孜及樟木，北通格尔木。', NULL, '2021-05-14 21:21:51', NULL);

-- ----------------------------
-- Table structure for users
-- ----------------------------
DROP TABLE IF EXISTS `users`;
CREATE TABLE `users`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `email` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `password` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `remember_token` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `is_locked` tinyint(4) NULL DEFAULT NULL COMMENT '状态： 0正常，1禁用',
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 14 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

